dict.string:
function notification { ./notificator --message "${1}" --title "${alfred_workflow_name}" }
# Kill process tree to stop upload and prevent notification from showing success readonly process="$(pgrep -nf 'uploadfile_process_anchor')" readonly process_groud_id="$(ps -o pgid= "${process}")" kill -- "-${process_groud_id}"
# Play sound and show message afplay /System/Library/Sounds/Funk.aiff notification 'Upload canceled'

// Helpers
function envVar(varName) {
  return $.NSProcessInfo
    .processInfo
    .environment
    .objectForKey(varName).js
}

function readFile(path) {
  return $.NSString.stringWithContentsOfFileEncodingError(
    path, $.NSUTF8StringEncoding, undefined).js
}

function activeProcess(anchor) {
  const task = $.NSTask.alloc.init

  task.executableURL = $.NSURL.fileURLWithPath("/usr/bin/pgrep")
  task.arguments = ["-qf", anchor]
  task.launchAndReturnError(false)

  task.waitUntilExit
  return task.terminationStatus === 0
}

// Main
function run() {
  if (!activeProcess("uploadfile_process_anchor")) return JSON.stringify({
    rerun: 1,
    items: [{
      title: "No Upload in Progress",
      subtitle: "Will auto-refresh if an upload starts",
      valid: false
    }]
  })

  const cacheDir = $(envVar("alfred_workflow_cache"))
  const fileName = readFile(cacheDir.stringByAppendingPathComponent("name.txt").js)
  const progress = readFile(cacheDir.stringByAppendingPathComponent("progress.txt").js)
    .split(" ").slice(-1)

  return JSON.stringify({
    rerun: 1,
    items: [{
      title: `${envVar("alfred_workflow_name")} Progress`,
      subtitle: `${progress} · ${fileName}`,
      valid: false,
      mods: { cmd: { valid: true } }
    }]
  })
}
